    private void txt_routeNumFocusLost(java.awt.event.FocusEvent evt) {//GEN-FIRST:event_txt_routeNumFocusLost
     if (txt_routeNum.getText().equals("")) {
        txt_routeNum.setText("Example Text");
        txt_routeNum.setForeground(Color.GRAY);
    }
     txt_routeNum.selectAll();
    }//GEN-LAST:event_txt_routeNumFocusLost

