    private void btn_scheduleActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn_scheduleActionPerformed
         String Username=Login.txt_UserName.getText();
         String code=txt_code.getText();
         
         
        {try{
     String dpath="jdbc:mysql://localhost:3306/router";
        Connection con=DriverManager.getConnection(dpath,"root","");
            Statement st=con.createStatement();
            ResultSet rst=st.executeQuery("select*from sch where code='" + code + "' ");
             
            while(rst.next())
            {
            
                
            String Busno=rst.getString("Bus_Num");
            String RouteNo=rst.getString("Route_Num");
            String Route=rst.getString("Route");
            String day=rst.getString("Day");
            String Stime=rst.getString("Time");
            String sPoint=rst.getString("Start");
            String dname=rst.getString("Driver");
            String cName=rst.getString("Conductor");
            
            st.executeUpdate("INSERT INTO `schcus`(`Bus_Num`, `Day`, `Time`, `Start`, `Route`, `Route_Num`, `Driver`, `Conductor`, `Username`) VALUES ('"+Busno+"','"+day+"','"+Stime+"','"+sPoint+"','"+Route+"','"+RouteNo+"','"+dname+"','"+cName+"','"+Username+"')");
             JOptionPane.showMessageDialog(null, "Scheduling the bs.Please wait for a moment..");
            CusTab backOwn=new CusTab();
        backOwn.setVisible(true);
        backOwn.txt_link.setText(Login.txt_UserName.getText());
        ImageIcon icon = new ImageIcon("E:/Project/yoo.png");
        backOwn.setIconImage(icon.getImage());
        dispose();
           
            
                    }
            
     }
     catch(SQLException ex)
     {System.out.println("Error");}}
    }//GEN-LAST:event_btn_scheduleActionPerformed

