    private void btn_nextActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn_nextActionPerformed
     String userName=txt_username.getText();
      String route=(String)cmb_route.getSelectedItem();
     String routeNum=txt_routeNum.getText();              // '"+routeNum+"','"+busNum+"','"+route+"','"+userName+"','"+dName+"','"+dLiNum+"','"+dConNubmer+"','"+cName+"','"+NIC+"','"+cConNum+"'
     String busNum=txt_busNo.getText();
     String dName=txt_drverName.getText();
     String dConNubmer=txt_contactDriver.getText();
     String dLiNum=txt_DNumber.getText();
     String cName=txt_nameConductor.getText();
     String cConNum=txt_contactConductor.getText();
     String NIC=txt_NIC.getText();
     if(routeNum.isEmpty())
     {JOptionPane.showMessageDialog(null, "Route number can not be empty");}
     else if(routeNum.matches("[a-zA-Z]+"))
     {JOptionPane.showMessageDialog(null, "Invalid route number");}
     else if (busNum.isEmpty())
     {JOptionPane.showMessageDialog(null, "Bus number can not be empty");}
     else if (dName.isEmpty())
     {JOptionPane.showMessageDialog(null, "Drivers's name can not be empty");}
      else if(dName.matches("[0-9]+"))
     {JOptionPane.showMessageDialog(null, "Invalid driver name");}
     else if(dConNubmer.matches("[a-zA-Z]+"))
     {JOptionPane.showMessageDialog(null, "Invalid driver number");}
     else if (dConNubmer.isEmpty())
     {JOptionPane.showMessageDialog(null, "Drivers's number can not be empty");}
     else if(dLiNum.matches("[a-zA-Z]+"))
     {JOptionPane.showMessageDialog(null, "Invalid driver license number");}
     else if (dConNubmer.isEmpty())
     {JOptionPane.showMessageDialog(null, "Drivers's license number can not be empty");}
     else if(dLiNum.matches("[a-zA-Z]+"))
     {JOptionPane.showMessageDialog(null, "Invalid driver License");}
     else if (dConNubmer.isEmpty())
     {JOptionPane.showMessageDialog(null, "Drivers's license number can not be empty");}
     else if(cName.matches("[0-9]+"))
     {JOptionPane.showMessageDialog(null, "Invalid conductor name");}
     else if (cName.isEmpty())
     {JOptionPane.showMessageDialog(null, "Conductor's name can not be empty");}
     else if(cConNum.matches("[a-zA-Z]+"))
     {JOptionPane.showMessageDialog(null, "Invalid conductor number");}
     else if (cConNum.isEmpty())
     {JOptionPane.showMessageDialog(null, "Conductor's number can not be empty");}
     else if(NIC.matches("[a-zA-Z]+"))
     {JOptionPane.showMessageDialog(null, "Invalid conductor NIC number");}
     else if (NIC.isEmpty())
     {JOptionPane.showMessageDialog(null, "Conductor's NIC number can not be empty");}
     else if (userName.isEmpty())
     {JOptionPane.showMessageDialog(null, "Owners user name can not be empty");} 
     else 
     { try{
      String dpath="jdbc:mysql://localhost:3306/Router";
         Connection con = DriverManager.getConnection(dpath,"root","");
          Statement st=con.createStatement();
          ResultSet rst = st.executeQuery("SELECT User_Name FROM owner WHERE User_Name='" + userName + "'");
          while (rst.next()){
          String uname = rst.getString("User_Name");
    if(userName.equals(uname))
            { JOptionPane.showMessageDialog(null, "Saving the data...");
            st.executeUpdate("INSERT INTO busreg (`Route_No`, `Bus_no`, `Route`, `Username`, `Driver_name`, `Driver_License`, `Driver_Contact`, `Conductor_name`, `Conductor_NIC`, `Conductor_Contact`) VALUES ('"+routeNum+"','"+busNum+"','"+route+"','"+userName+"','"+dName+"','"+dLiNum+"','"+dConNubmer+"','"+cName+"','"+NIC+"','"+cConNum+"')");
            dispose();
            ownTab backOwn=new ownTab();
            backOwn.txt_welcomelink.setText(LoginOwner.txt_UserName.getText());
            backOwn.setVisible(true);
             ImageIcon icon = new ImageIcon("E:/Project/yoo.png");
     backOwn.setIconImage(icon.getImage());
            
            //String query = "CREATE TABLE " + busNum + " (column1 datatype, column2 datatype, column3 datatype)";
            
            }
    else 
    {JOptionPane.showMessageDialog(null, "Incorrect User Name");}
          }
          
      }
      catch(SQLException ex)
      {JOptionPane.showMessageDialog(null, "This bus is already registered");}
      
      
     }
     
     
     
     
     
    }//GEN-LAST:event_btn_nextActionPerformed

